INSERT INTO animals (animal, picture, min, max, reasons) VALUES ('Goldfish', 'http://elizabeth.podgorny.us/wp-content/uploads/2015/05/goldfish.png', 0, 5, 'Start small!  Maybe even start with a plant... Like a cactus.  You should probably work on yourself before you take care of something else.  You are flat out irresponsible!');
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Kitty Cat", "http://4.bp.blogspot.com/_BhTZERbxx54/TAC1mCB53BI/AAAAAAAAACs/cLcnffFYgws/s1600/funny+cat+wallpaper.jpg", 6, 10, "Miss Independent.  You're fairly responsible, keep working on it until then here's a cat!");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Poodle", "https://www.askideas.com/media/17/ColorfulPoodle-Dog-Picture.jpg", 11, 12, "You can't stand dog hair on your clothes or furniture.  You are probably over 60 years old.  You probably like to dye your hair crazy colors.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Chihuahua", "https://themerfett.files.wordpress.com/2015/05/chihuahua.jpg", 13, 14, "You really want a rodent as a pet.  You're scared of everything but will bite the hell out of everyone.  You constantly have the nervous shakes.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Pomeranian", "http://www.rasedecaini.com/wp-content/uploads/2009/12/pomeranian-02.jpg", 15, 16, "You have bad taste.  You're content with yappy animals and dogs that spend a majority of their day with their tongue in their ass.  You are probably Seif.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Pug", "https://s-media-cache-ak0.pinimg.com/originals/f8/3c/41/f83c41752663bc9d30a042bb85767091.jpg", 17, 18, "Sleep apnea is a thing in your life.  You are a big snorer.  You're obsessive.";
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Great Dane", "http://stunningfun.com/uploads/posts/2016-05/1464171109_24.jpg", 19, 20, "You like having a giant roommate that thinks they're tiny.  You want a lap dog. You don't mind sharing your whole bed.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Australian Shepherd", "https://i.pinimg.com/originals/19/29/6a/19296a66de36933b1b989c2566522bd4.jpg", 21, 22, "You probably have a massive yard.  You're a hiker and want a hiking buddy.  You constantly feel like you need to be doing something, like running.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Labrador", "https://i.pinimg.com/originals/f7/b0/d1/f7b0d1604d614ca5c852b60c9f9a7d4e.jpg", 23, 24, "Your furniture doesn't mean much to you.  You're very hyper and hate small spaces.  Your favorite pasttime is chewing on the baseboards.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Golden Retriever", "http://www.storytrender.com/wp-content/uploads/2018/02/6_CATERS_DOUBLE_DOG_TREAT_CATCH_07-1024x768.jpg", 25, 26, "You're white.  You like pumpkin spice lattes and Ugg boots.  You're a basic bitch.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("Saint Bernard", "https://usatunofficial.files.wordpress.com/2012/05/st-bernard-dogs-13005207-1024-768.jpg", 27, 28, "You feel like a hero when you're in the snow.  You like classical music.  You absolutely love to have pools of drool all over your house.");
INSERT INTO animals (animal, picture, min, max, reasons) VALUES ("French Bulldog", "https://pbs.twimg.com/media/B1X3Kf6IgAAUCye.jpg", 29, 30, "You're probably a gay, male, tech worker living in San Francisco.  You say 'YAAAAAAAAAAAAS'... Probably way too much.  You have daddy issues.");
